<!DOCTYPE html><html><head>    <meta http-equiv="Content-Type" content="text/html;charset=utf-8">    <title>jQuery UI Signature Basics</title>    <link href="http://ajax.googleapis.com/ajax/libs/jqueryui/1.9.0/themes/south-street/jquery-ui.css" rel="stylesheet">    <link href="sigLib/css/jquery.signature.css" rel="stylesheet">    <style>        .kbw-signature {            width: 400px;            height: 200px;        }    </style>    <!--[if IE]>    <![endif]-->    <script src="http://ajax.googleapis.com/ajax/libs/jquery/1.8.2/jquery.min.js"></script>    <script src="http://ajax.googleapis.com/ajax/libs/jqueryui/1.9.0/jquery-ui.min.js"></script>    <script src="sigLib/js/jquery.signature.js"></script>    <script>        function xxx(){            $('#signatureJSON').text($('#sig').signature('toDataURL'))        }        $(function () {            $('#sig').signature();            $('#clear').click(function () {                Clear();            });            $('#json').click(function () {                $.ajax({                    url: 'insert_draw.php',                    type: 'POST',                    data: {                        txtName:$('#txtName').val() ,                        Draw:$("#signatureJSON").val()            },                    datatype: 'json',                    success: function (result) {                        var obj = jQuery.parseJSON(result);                        if (obj['Status'] == 'Success') {                            window.location.href="signature.php"                        }                    }                });                //alert($('#sig').signature('toJSON'));            });            function Clear() {                $('#txtName').val('');                $('#sig').signature('clear');            }        });    </script></head><body><table>    <tr>        <td>Name :</td>        <td><input type="text" id="txtName" placeholder="input you name"><br/></td>    </tr>    <tr>        <td>Signature</td>        <td>            <div id="sig" onclick="xxx()"></div>            <textarea  id="signatureJSON" rows="5" cols="50" class="ui-state-active"></textarea>        </td>    </tr>    <tr>        <td colspan="2">            <button id="clear">Clear</button>            <button id="json">Insert</button>        </td>    </tr></table></body></html>